import React, {useState, useRef, useEffect} from 'react';

let timerInterval = null;
const initalTimer = {min: 0, sec: 0, milli: 0};
export default function Home() {
    const [time, setTime] = useState(initalTimer);
    const [timer, setTimer] = useState(0);

    useEffect(() => {
      const { min, sec, milli } = time || {};
      let newSec = timer !== 0 && timer%1000 === 0 ? sec + 1 : sec;
      let newMin = newSec !== 0 && newSec%60 === 0 ?  min + 1 : min;
      const updatedTimer = {
        min: newMin,
        sec: newSec,
        milli: newSec === 0 ? 0 : timer,
      }
      if(newSec === 0){
        setTimer(0);
      };
      setTime(updatedTimer);
    }, [timer]);

    const handleStartTimer = () => {
      timerInterval = setInterval(() => {
        console.log("timer");
        setTimer(prevTimer => prevTimer + 1);
      }, 10);
    };
    const handleStopTimer = () => {
      clearInterval(timerInterval);
      setTime(initalTimer);
      setTimer(0);
    };
    return (<div>
        <p>{time.min}:{time.sec}:{time.milli}</p>
        <div>
          <button onClick={handleStartTimer}>start</button>
          <button onClick={handleStopTimer}>stop</button>
        </div>
    </div>)
}
