import React, {useState, useEffect} from 'react';

let timerInterval = null;
const initalTimer = {min: 0, sec: 0, milli: 0};
export default function Home() {
    const [time, setTime] = useState(initalTimer);
    const [timer, setTimer] = useState(0);

    useEffect(() => {
            const { min, sec, milli } = time || {};
            let newSec = (timer !== 0 && timer % 100 === 0) ? sec + 1 : sec;
            let newMin = (newSec !== 0 && newSec % 60 === 0) ?  min + 1 : min;
            let updatedTimer = {
                min: newMin,
                sec: newSec,
                milli: newSec === 0 ? 0 : timer,
            }
            if(newSec === 0){
                // setTimer(0);
            };
            setTime(updatedTimer);
    }, [timer]);


    const handleStartTimer = () => {
      timerInterval = setInterval(() => {
        setTimer(prevTimer => (prevTimer % 100 === 0) ?  0:prevTimer + 1);
      }, 10);
    };

    const handleStopTimer = () => {
      setTimer(0);
      setTime(initalTimer);
      clearInterval(timerInterval);
    };
    return (<div>
        <p>{time.min}:{time.sec}:{time.milli} {timer}</p>
        <div>
          <button onClick={e => handleStartTimer(e)}>start</button>
          <button onClick={e => handleStopTimer(e)}>stop</button>
        </div>
    </div>)
}
