import React, { useRef, useState } from 'react';
import './App.css';
import SQLFrom from '../src/components/SQLFrom/SQLFrom';
import TableData from '../src/components/TableData/TableData';
import { SQLData } from '../src/utils/utils';

const App = props => {

  const [queryData, setQueryData] = useState({});

  const textAreaRef = useRef(null);
  const handleFromSubmit = (e) =>{
    e.preventDefault();
    let selectedQuery = {};
    data.forEach(el => {
      if(el.query === textAreaRef?.current?.value){
        selectedQuery = el;
      }else{
        selectedQuery = el;
      }
      setQueryData(el);
    });

  }

  return (
      <div className="container">
           <SQLFrom 
           textAreaRef={textAreaRef}
           handleFromSubmit={handleFromSubmit}
           />
           <TableData tableData={queryData?.tableData}/>
      </div>
  );
}


export default App;
