import React, { useEffect, useReducer } from 'react';
import { connect } from 'react-redux';
import { showMyLearningToolTip } from '../src/store/reduxTest/ReduxTestActions';
import './App.css';
import { useSelector, useDispatch } from 'react-redux';
import  reducer  from '../src/store/reducer';
import axios from 'axios';

const App = props => {

  const [category, setCategory] = useState(null);
  const [state, dispatch] = useReducer(reducer, {
    showMyLearningTooltip: false 
})
// console.log("state: ", state);
  // const dispatch = useDispatch();
  // let showMyLearningToolTip = useSelector(state => state.myLearningToolTip.showMyLearningTooltip);

  const getCategories = async (url) =>{
    try{
      let resp = await axios.get(url);
      if(resp?.data?.data){
        return resp.data.data;
      }
    }catch(err){
      console.log(err);
    }
  }

  useEffect(async () => {
   let categories = await getCategories("https://60891feca6f4a30017427aa2.mockapi.io/api/v1/ftask/categories");
   let category = categories[0];
   console.log("categories", category);


  }, [])
  return (

        <div className="App">
          Kishore
        </div>
  );
}


const mapStateToProps = state => ({
  showMyLearningTooltip : state.showMyLearningTooltip
});

const mapDispatchToProps = dispatch => ({
  showMyLearningToolTip: value => dispatch(showMyLearningToolTip(value))
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
