import React, {useState, useRef } from 'react';
import './App.css';
const App = props => {
  const [data, setData] = useState([])
  const inputRef = useRef(null)

  const handleFromSubmit = (e) =>{
      e.preventDefault();
      let changeData = [...data] || [];
      if(inputRef?.current?.value){
        changeData.push(inputRef?.current?.value);
        setData(changeData);
        inputRef.current.value = "";
      }
  }

  const handleDelete = (index) =>{
    let myArr = [...data];
    myArr =  myArr.filter((el, i) => i != index);
    setData(myArr);
  }

  return (
      <div className="container">
            Kishore
      </div>
  );
}


export default App;
