import React, {useState, useRef } from 'react';
import './App.css';
const App = props => {
  const [data, setData] = useState([])
  const inputRef = ususeRefeState(null)

  const handleFromSubmit = (e) =>{
      e.preventDefault();
      let changeData = [...data] || [];
      if(inputRef?.current?.value){
        changeData.push(inputRef?.current?.value);
        setData(changeData);
        inputRef.current.value = "";
      }
  }

  const handleDelete = (index) =>{
    let myArr = [...data];
    myArr =  myArr.splice(index)
    setData(myArr);
  }

  return (
      <div className="container">
        <div className="form-section">
            <form onSubmit={e => handleFromSubmit(e)}> 
                  <input className="input-text" type="text" ref={inputRef} />
                  <button type="submit">Add Element</button>
            </form>
        </div>
        <div className="listing-section">
          {
            data?.map((el, index) =>{
                return (
                  <div key={el} className="card"> 
                       <div className="element">
                           {el}
                      </div>
                      <div className="delete" onClick={e => handleDelete(index)}>
                           Delete
                      </div>
                  </div>
                )
            })
          }
        </div>
      </div>
  );
}


export default App;
