import React, { useEffect, useReducer, useState } from 'react';
import { connect } from 'react-redux';
import { showMyLearningToolTip } from '../src/store/reduxTest/ReduxTestActions';
import './App.css';
import { useSelector, useDispatch } from 'react-redux';
import  reducer  from '../src/store/reducer';
import axios from 'axios';

const App = props => {

  const [mainCategory, setMainCategory] = useState(null);
  const [childSubCategories, setChildSubCategories] = useState(null);
  const [tabSelect, setTabSelect] = useState("ALL");
  const [state, dispatch] = useReducer(reducer, {
    showMyLearningTooltip: false 
})
console.log("main category: ", mainCategory);
console.log("child sub categoies: ", childSubCategories)
// console.log("state: ", state);
  // const dispatch = useDispatch();
  // let showMyLearningToolTip = useSelector(state => state.myLearningToolTip.showMyLearningTooltip);

  const getCategories = async (url) =>{
    try{
      let resp = await axios.get(url);
      if(resp?.data?.data){
        return resp.data.data;
      }
    }catch(err){
      console.log(err);
    }
  }

  useEffect(() => {
      getCategories("https://60891feca6f4a30017427aa2.mockapi.io/api/v1/ftask/categories").then(categories =>{
        let category = categories[0];
        setMainCategory(category);
      })
  }, [])

  const handleTabClick = (el) =>{
    setTabSelect(el);
  }

  useEffect(() =>{
    let subCategoryDummyIds = [];
    let promisesArray = [];
    if(category?.sub_categories?.length > 0){
      category.sub_categories.forEach(el => {
      subCategoryDummyIds.push(el.dummy_id);
      });
    if(subCategoryDummyIds?.length > 0){
      subCategoryDummyIds.forEach(id =>{
        let url = "https://60891feca6f4a30017427aa2.mockapi.io/api/v1/ftask/" +id
        promisesArray.push(getCategories(url));
      });
      if(promisesArray.length > 0){
        Promise.all(promisesArray).then(results =>{
          if(results.length > 0){
            let subArray = [];
            results.forEach(el =>{
              subArray = subArray.concat(el);
            })
            setChildSubCategories(subArray);
          }
        })
      }
    }
    }


  }, [tabSelect]);
  return (

        <div className="App">
            <div className="category-name">
                {mainCategory?.category}
            </div>
            <div style={{marginBottom: "30px", display:"flex"}}>
               <div style={{ marginRight: "30px"}} onClick={e => handleTabClick("ALL")} className={ tabSelect === "ALL" ? "sub-categoies active" :  "sub-categoies"}>All</div>
                {
                  mainCategory?.sub_categories?.map(el =>{
                    console.log("element: ", el)
                      return(
                        <div onClick={e => handleTabClick(el?.name)}  style={{ marginRight: "30px"}} className={ tabSelect === el.name ? "sub-categoies active" :  "sub-categoies"}>
                            {el.name}
                        </div>
                      )
                  })
                }
            </div>

        </div>
  );
}


const mapStateToProps = state => ({
  showMyLearningTooltip : state.showMyLearningTooltip
});

const mapDispatchToProps = dispatch => ({
  showMyLearningToolTip: value => dispatch(showMyLearningToolTip(value))
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
