import React, { useEffect, useReducer, useState } from 'react';
import './App.css';

let timeInterval;
let initialTime = { min:0, sec: 0, milli:0 }
const App = props => {
const [time, setTime] = useState(initialTime);
const [timer, setTimer] = useState(0);

useEffect(() =>{
      const { min, sec } = time || {};
      let newSec = (timer !==0 && timer % 60 === 0) ?  sec+1: sec;
      let newMin = (newSec !== 0 && newSec % 60 === 0) ? min + 1: min;
      let updateTime = {
        min:newMin,
        sec: newSec,
        milli:timer
      }
}, [timer])

const startTimer = () =>{
  timeInterval = setInterval(() => {
      setTimer(prev => (prev === 100) ? 0 : prev + 1);
  }, 10);
}

const pauseTimer = () =>{
    clearInterval(timeInterval);
}

const resetTimer = () =>{
    clearInterval(timeInterval);
    setTimer(0)
    setTime(initialTime);
}

  return (
      <div>
          <div>{time.hr} : {time.min}: {time.sec}</div>
          <div onClick={startTimer}>
            Start/Resume
          </div>
          <div onClick={pauseTimer}>
             Pause
          </div>
          <div onClick={resetTimer}>
             Reset
          </div>
      </div>
  );
}


export default App;
