import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

const [value, setValue] = useState(null);
const [rangeArray, setRangeArray] = useState([]);

  const handleInputChange = (e) =>{
    console.log(e?.target?.value)
        setValue(e.target.value);
  }

  useEffect(() =>{
    let arr = [];
     if(!!value){
        for(var i=1;i<value;i++){
            arr.push(i);
        }
     }
     setRangeArray(arr); 
  }, [value]);

  const handleNumberClick = (index) =>{
      let newArray = rangeArray;
      if(rangeArray[index] % 3 === 0 && rangeArray[index] % 5 === 0){
        newArray[index] = "ClearFeed";
      }else if(rangeArray[index] % 5 === 0){
        newArray[index] = "Clear";
      }else if(rangeArray[index] % 3 === 0){
        newArray[index] = "Feed";
      }
      console.log("new array: ", newArray)
      setRangeArray([...newArray]);
  }

  return (
    <div className="App">
           <input type="number" value={value} onChange={handleInputChange}/>
           {
             rangeArray?.map((el, index) =>{
               return(
                  <div onChange={e => handleNumberClick(index)}>{el}</div>
               )
             })
           }
    </div>
  );
}

export default App;
