import React, { useState, useEffect } from 'react';
import { Provider } from 'react-redux';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import  { store } from './store/store';

const App = () => {

  const [arr, setArr] = useState([1,2,3,4,5])

  const [obj, setObj] = useState({name:"Kishore", place:"Ap"})



  const handleInputChange = (el, index) =>{
    let newArray = arr;
    newArray[index] = "Rajesh"
    setArr([...newArray]);
  }

  const getElements = () =>{
    let finalArr = [];
    arr.forEach((el, index) =>{
     finalArr.push(<div className="name-holder" onClick={e => handleInputChange(el, index)}>{el}</div>)
    })
    return finalArr;
  }

  const handleObjChange = () =>{
   setObj((prev) =>{
     return(
       {
        ...prev, name:"Chinna"
       }
     )
    });
  }

  const getObjectValue = () =>{
      return(
        <div className="obj-holder" onClick={e => handleObjChange(e)}>
            {obj?.name} {obj?.place}
        </div>
      )
  }

  return (
    // <Provider store={store}>
    //   <div className="App">
    //           <div>Kishore</div>
    //       </div>
    // </Provider>

        <div className="App">
        <div className="divContainer">
            {getElements()}
        </div>
        <div className="objectContainer">
            {getObjectValue()}
        </div>
    </div>

  );
}

export default App;
