import React, { useEffect, useReducer, useState } from 'react';
import './App.css';

let timeInterval;
let initialTime = { hr:0, min:0, sec: 0 }
const App = props => {
const [time, setTime] = useState(initialTime);
const [timer, setTimer] = useState(0);


const startTimer = () =>{
  timeInterval = setTimeout(() => {
      setTimer(prev => (prev === 100) ? 0 : prev + 1);
  }, 10);
}

const pauseTimer = () =>{
    clearInterval(timeInterval);
}

const resetTimer = () =>{
    setTimer(0)
    clearInterval(timeInterval);
    setTime(initialTime);
}

  return (
      <div>
          <div>{time.hr} : {time.min}: {time.sec}</div>
          <div onClick={startTimer}>
            Start/Resume
          </div>
          <div onClick={pauseTimer}>
             Pause
          </div>
          <div onClick={resetTimer}>
             Reset
          </div>
      </div>
  );
}


export default App;
