import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { showMyLearningToolTip } from '../src/store/reduxTest/ReduxTestActions';
import './App.css';
import { useSelector, useDispatch } from 'react-redux';

const App = props => {

  const showMyLearningToolTip = useSelector(state.showMyLearningTooltip);
  console.log("tesst", showMyLearningToolTip);
  useEffect(() => {
      props.showMyLearningToolTip(true)
  }, [props])
  return (

        <div className="App">
          Kishore
        </div>
  );
}


const mapStateToProps = state => ({
  showMyLearningTooltip : state.showMyLearningTooltip
});

const mapDispatchToProps = dispatch => ({
  showMyLearningToolTip: value => dispatch(showMyLearningToolTip(value))
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
