import React, {useState, useEffect} from 'react';

let timerInterval = null;
const initalTimer = {min: 0, sec: 0, milli: 0};
export default function Home() {
    const [timer, setTimer] = useState(0);
    const [time, setTime] = useState(initalTimer);

    useEffect(() => {
      const { min, sec } = time || {};
      let newSec = timer !== 0 && timer % 100 === 0 ? sec + 1 : sec;
      let newMin = newSec !== 0 && newSec % 60 === 0 ? min + 1 : min;
      let updateTime = {
        min: newMin,
        sec: newSec,
        milli: timer
      };
      if (newSec === 0) {
        setTime(initalTimer);
      }
      setTime(updateTime);
    }, [timer]);

    const startTimer = () => {
        timerInterval = setInterval(() => {
        setTimer((p) => (p === 100 ? 0 : p + 1));
      }, 10);
    };

    const pauseTimer = () => {
      clearInterval(timerInterval);
    };

    const resetTimer = () => {
      clearInterval(timerInterval);
      setTime(initalTimer);
      setTimer(0);
    };

    return (
      <div className="App">
        <div>
          {time?.min}:{time?.sec}:{time?.milli}
        </div>
        <div onClick={(e) => startTimer()}>start</div>
        <div onClick={(e) => pauseTimer()}>Pause</div>
        <div onClick={(e) => startTimer()}>Resume</div>
        <div onClick={(e) => resetTimer()}>Reset</div>
      </div>
    )

}
