import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

const [value, setValue] = useState(null);
const [rangeArray, setRangeArray] = useState([]);

  const handleInputChange = (e) =>{
    console.log(e.target.value)
    if(e?.target?.value){
      setTimeout(()=>{
        setValue(e.target.value);
      },500)
    }
  }

  useEffect(() =>{
    let arr = [];
     if(!!value){
        for(var i=1;i<=i;i++){
            arr.push(i);
        }
     }
     setRangeArray(arr); 
  }, [value]);

  return (
    <div className="App">
           <input type="number" value={value} onChange={e => handleInputChange(e)}/>
           {/* {
             rangeArray?.map(el =>{
               return(
                  <div>{el}</div>
               )
             })
           } */}
    </div>
  );
}

export default App;
