import React from 'react'

const QueryToggle = ({ sqlData }) => {

    return (
        <div>
            <select value={selectedCategory} onChange={(e) => handleCategoryChange(e.target.value)}>
                {
                    sqlData?.map((el, index)=>{
                        return <option selected={index === 0 ? true:null} key={el.query} value={index}>{el.category}</option>
                    })
                }
            </select>
        </div>
    )
}

export default QueryToggle;
